package br.ucs.cooklist;

import android.content.Intent;
import android.graphics.BitmapFactory;
import android.os.Bundle;
import android.widget.Button;
import android.widget.EditText;

import androidx.appcompat.app.AppCompatActivity;
import androidx.recyclerview.widget.LinearLayoutManager;
import androidx.recyclerview.widget.RecyclerView;

import java.util.ArrayList;
import java.util.List;

import br.ucs.cooklist.adapter.ReceitaAdapter;
import br.ucs.cooklist.helper.DatabaseHelper;
import br.ucs.cooklist.helper.ImageHelper;
import br.ucs.cooklist.model.Ingrediente;
import br.ucs.cooklist.model.Receita;

public class MainActivity extends AppCompatActivity {

    private RecyclerView rcReceitas;
    private Button btnNovaReceita;
    private ReceitaAdapter receitaAdapter;
    private DatabaseHelper databaseHelper;

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_main);
        init();

        List<Receita> lstReceitas = getDatabaseHelper().buscarReceitas();
        receitaAdapter.getLstReceitas().addAll(lstReceitas);
        receitaAdapter.notifyDataSetChanged();
    }

    private void init() {
        getSupportActionBar().setTitle("Receitas");

        receitaAdapter = new ReceitaAdapter(this);
        inputPesquisa = findViewById(R.id.inputPesquisa);
        btnPesquisar = findViewById(R.id.btnPesquisar);
        btnNovaReceita = findViewById(R.id.btnNovaReceita);
        rcReceitas = findViewById(R.id.rcReceitas);
        rcReceitas.setAdapter(receitaAdapter);
        rcReceitas.setLayoutManager(new LinearLayoutManager(this));

        addEvents();
    }

    private void addEvents() {
        btnNovaReceita.setOnClickListener(view -> {
            Intent intent = new Intent(MainActivity.this, CadastroReceitaActivity.class);
            startActivity(intent);
        });

        receitaAdapter.setActions((view, position) -> {
            Receita receita = receitaAdapter.getLstReceitas().get(position);
            Intent intent = new Intent(MainActivity.this, CadastroReceitaActivity.class);

            // não será mais usado pois o objeto sendo passado pode ser muito grande.
            // intent.putExtra("receita", receita.toBundle());
            intent.putExtra("codReceita", receita.getCodReceita());
            startActivity(intent);
        });
    }

    private List<Receita> buscarReceitas() {
        List<Receita> receitas = new ArrayList<>();
        String img64 = "";
        for (int i = 0; i < 10; i++) {
            Receita r = new Receita();
            r.setCodReceita(i);
            r.setNomeReceita("receita " + i);
            r.setDesReceita("Um receita muito deliciosa de creme morando chocalete creme asdokasdpojasdlkasjdlkjasdlkjasdlkdj");

            if (i % 2 == 0) {
                r.setBase64Receita(img64);
            } else {
                r.setBase64Receita(ImageHelper.toBase64(BitmapFactory.decodeResource(getResources(), R.drawable.placeholder)));
            }

            for (int j = 0; j < 5; j++) {
                Ingrediente ing = new Ingrediente();
                ing.setCodReceita(r.getCodReceita());
                ing.setCodIngrediente(j + ing.getCodReceita());
                ing.setDescIngrediente("Ingrediente: " + j);
                r.getLstIngredientes().add(ing);
            }
            receitas.add(r);
        }
        return receitas;
    }

    private DatabaseHelper getDatabaseHelper() {
        if (databaseHelper == null) {
            databaseHelper = new DatabaseHelper(this);
        }
        return databaseHelper;
    }

}